import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const submit = (e) => { e.preventDefault(); alert(`Logged in as ${email}`); };

  return (
    <div className="page">
      <div className="max-w-md card mx-auto">
        <h2 className="text-2xl font-semibold mb-4">Login</h2>
        <form className="grid gap-3" onSubmit={submit}>
          <input className="border rounded-xl px-4 py-2.5" placeholder="Email" type="email" value={email} onChange={e=>setEmail(e.target.value)} />
          <input className="border rounded-xl px-4 py-2.5" placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
          <button className="btn btn-primary" type="submit">Login</button>
        </form>
        <p className="text-sm text-gray-600 mt-3">No account? <Link className="text-primary underline" to="/register">Register</Link></p>
      </div>
    </div>
  );
}
