import React, { useState, useEffect } from "react";

/**
 * Bootstrap 5 contact form with:
 * - live validation
 * - is-valid / is-invalid classes
 * - loading spinner during submit
 * - success alert after submit
 * - example fetch to send data to backend, including uploaded image URL
 *
 * NOTE: uploaded image path (local) included below as `uploadedImageUrl`.
 * Your environment should transform that into a real URL when sending/serving the file.
 */

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [isValid, setIsValid] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [successMsg, setSuccessMsg] = useState("");

  // Uploaded file path provided by you (will be transformed to a URL by your system)
  const uploadedImageUrl =
    "/mnt/data/32a66d3a-773b-40a0-bc27-817d1d889c36.png";

  const validate = (values) => {
    const newErrors = {};
    if (!values.name.trim()) newErrors.name = "Name is required";
    if (!values.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/^\S+@\S+\.\S+$/.test(values.email)) {
      newErrors.email = "Enter a valid email";
    }
    if (!values.message.trim()) {
      newErrors.message = "Message is required";
    } else if (values.message.trim().length < 10) {
      newErrors.message = "Message must be at least 10 characters";
    }
    return newErrors;
  };

  // Live validate
  useEffect(() => {
    const newErrors = validate(form);
    setErrors(newErrors);
    setIsValid(Object.keys(newErrors).length === 0);
  }, [form]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched((p) => ({ ...p, [name]: true }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate(form);
    setErrors(newErrors);
    setTouched({ name: true, email: true, message: true });

    if (Object.keys(newErrors).length) return;

    try {
      setSubmitting(true);
      setSuccessMsg("");

      // Example payload to send to your backend.
      // NOTE: replace '/api/contact' with your real endpoint.
      // The uploaded file path is included in `imageUrl` — transform it to a URL on your server as needed.
      const payload = {
        name: form.name.trim(),
        email: form.email.trim(),
        message: form.message.trim(),
        imageUrl: uploadedImageUrl,
      };

      // Example fetch (uncomment and adapt to actually send)
      /*
      const res = await fetch("/api/contact", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error("Network response was not ok");
      const data = await res.json();
      */

      // Simulate network delay / success for demo
      await new Promise((r) => setTimeout(r, 900));

      // On success:
      setSuccessMsg("Thanks — your message has been sent successfully!");
      setForm({ name: "", email: "", message: "" });
      setTouched({});
    } catch (err) {
      // handle error (show toast, set field errors, etc.)
      setSuccessMsg("Something went wrong. Please try again later.");
      console.error(err);
    } finally {
      setSubmitting(false);
    }
  };

  // Builds class string including is-valid / is-invalid
  const inputClass = (field) => {
    const base = "form-control";
    if (errors[field] && touched[field]) return `${base} is-invalid`;
    if (!errors[field] && touched[field]) return `${base} is-valid`;
    return base;
  };

  return (
    <div className="container py-5">
      <div className="col-lg-6 mx-auto p-4 shadow rounded bg-white">
        <h2 className="mb-4">Contact Us</h2>

        {/* success / status alert */}
        {successMsg && (
          <div
            className={`alert ${
              successMsg.startsWith("Thanks") ? "alert-success" : "alert-danger"
            }`}
            role="alert"
          >
            {successMsg}
          </div>
        )}

        <form onSubmit={handleSubmit} noValidate>
          {/* Name */}
          <div className="mb-3">
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              className={inputClass("name")}
              value={form.name}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={!!errors.name}
              aria-describedby={errors.name ? "name-error" : undefined}
            />
            {errors.name && touched.name && (
              <div id="name-error" className="invalid-feedback">
                {errors.name}
              </div>
            )}
          </div>

          {/* Email */}
          <div className="mb-3">
            <input
              type="email"
              name="email"
              placeholder="Email"
              className={inputClass("email")}
              value={form.email}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={!!errors.email}
              aria-describedby={errors.email ? "email-error" : undefined}
            />
            {errors.email && touched.email && (
              <div id="email-error" className="invalid-feedback">
                {errors.email}
              </div>
            )}
          </div>

          {/* Message */}
          <div className="mb-3">
            <textarea
              name="message"
              placeholder="Message"
              rows="4"
              className={inputClass("message")}
              value={form.message}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={!!errors.message}
              aria-describedby={errors.message ? "message-error" : undefined}
            ></textarea>
            {errors.message && touched.message && (
              <div id="message-error" className="invalid-feedback">
                {errors.message}
              </div>
            )}
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="btn btn-primary w-100 d-flex justify-content-center align-items-center"
            disabled={!isValid || submitting}
            aria-disabled={!isValid || submitting}
          >
            {submitting ? (
              // spinner (Bootstrap)
              <>
                <span
                  className="spinner-border spinner-border-sm me-2"
                  role="status"
                  aria-hidden="true"
                ></span>
                Sending...
              </>
            ) : (
              "Send Message"
            )}
          </button>
        </form>

        <small className="text-muted d-block mt-3">
          Note: uploaded image path included for backend:{" "}
          <code>{uploadedImageUrl}</code>
        </small>
      </div>
    </div>
  );
}
