import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]); // merged view shown in UI
  const [jsonEvents, setJsonEvents] = useState([]); // events from events.json
  const [localEvents, setLocalEvents] = useState([]); // events user added/edited (persisted)
  const [newEvent, setNewEvent] = useState({ title: "", date: "", location: "", desc: "" });

  const [editIndex, setEditIndex] = useState(null);
  const [editEvent, setEditEvent] = useState({ title: "", date: "", location: "", desc: "" });

  // thumbnail path you uploaded (will be transformed to a served URL by your environment)
  const uploadedImageUrl = "/mnt/data/83ef8988-4e1a-4cb2-b6c4-7062b81cfdf6.png";

  const LOCAL_KEY = "events_local";        // only local/user events saved here
  const JSON_RAW_KEY = "events_json_raw";  // raw JSON string for change detection

  // helper: event key for dedupe (title + date)
  const eventKey = (ev) => `${(ev.title || "").trim()}|${(ev.date || "").trim()}`;

  // merge function: json first, then local events that aren't duplicates
  const buildMerged = (jsonArr = [], localArr = []) => {
    const map = new Map();
    // add json events
    jsonArr.forEach((ev) => {
      map.set(eventKey(ev), { ...ev, source: "json" });
    });
    // add local events (will overwrite json if same key)
    localArr.forEach((ev) => {
      map.set(eventKey(ev), { ...ev, source: "local" });
    });
    // preserve order: json items in their order, then any purely-local ones that weren't in json
    const merged = [];
    jsonArr.forEach((ev) => {
      const k = eventKey(ev);
      if (map.has(k)) merged.push(map.get(k));
      map.delete(k);
    });
    // remaining map entries are local-only (or local overrides). append them in insertion order:
    for (const val of map.values()) merged.push(val);
    return merged;
  };

  // Load json + local events and merge. Detect changes to events.json by comparing its raw string.
  useEffect(() => {
    // load local events from storage first (so UI has something quickly)
    const savedLocalRaw = localStorage.getItem(LOCAL_KEY);
    let savedLocal = [];
    if (savedLocalRaw) {
      try {
        const parsed = JSON.parse(savedLocalRaw);
        if (Array.isArray(parsed)) savedLocal = parsed;
      } catch (err) {
        console.warn("Invalid local events - clearing local copy", err);
        savedLocal = [];
      }
    }
    setLocalEvents(savedLocal);

    // fetch events.json (no cache)
    fetch("/events.json", { cache: "no-store" })
      .then((res) => {
        if (!res.ok) throw new Error("events.json not found");
        return res.text();
      })
      .then((raw) => {
        // raw string used for change detection
        const prevRaw = localStorage.getItem(JSON_RAW_KEY);
        // parse it safely
        let parsedJson = [];
        try {
          parsedJson = JSON.parse(raw);
          if (!Array.isArray(parsedJson)) parsedJson = [];
        } catch (err) {
          console.warn("events.json parse error", err);
          parsedJson = [];
        }

        // if json changed (or never stored), update stored raw string
        if (raw !== prevRaw) {
          localStorage.setItem(JSON_RAW_KEY, raw);
        }

        setJsonEvents(parsedJson);

        // rebuild merged view using parsedJson + savedLocal
        const merged = buildMerged(parsedJson, savedLocal);
        setEvents(merged);
      })
      .catch((err) => {
        // if fetch fails, fall back to local events only
        console.warn("Could not fetch events.json:", err);
        setJsonEvents([]);
        const merged = buildMerged([], savedLocal);
        setEvents(merged);
      });
  }, []);

  // persist local events helper
  const persistLocal = (locals) => {
    setLocalEvents(locals);
    try {
      localStorage.setItem(LOCAL_KEY, JSON.stringify(locals));
    } catch (err) {
      console.warn("Could not save local events:", err);
    }
    // rebuild merged displayed list
    setEvents(buildMerged(jsonEvents, locals));
  };

  // Add new event (saved as local)
  const addEvent = (e) => {
    e.preventDefault();
    if (!newEvent.title || !newEvent.date) return;
    const added = { ...newEvent, source: "local" };
    const updatedLocal = [...localEvents, added];
    persistLocal(updatedLocal);
    setNewEvent({ title: "", date: "", location: "", desc: "" });
  };

  // Start edit - we provide index relative to merged events
  const startEdit = (index) => {
    setEditIndex(index);
    const ev = events[index];
    setEditEvent({ title: ev.title || "", date: ev.date || "", location: ev.location || "", desc: ev.desc || "" });
  };

  // Save edit — if the edited item is from JSON source we convert it into a local override
  const saveEdit = (e) => {
    e.preventDefault();
    if (!editEvent.title || !editEvent.date) return;

    const targetKey = eventKey(events[editIndex]);
    // replace or add in localEvents: if a local event shares the same key, replace it; else add new local override
    const localsCopy = [...localEvents];
    const localIdx = localsCopy.findIndex((le) => eventKey(le) === targetKey);

    const newLocal = { ...editEvent, source: "local" };
    if (localIdx >= 0) {
      localsCopy[localIdx] = newLocal;
    } else {
      // remove any JSON original with same key from merged view by adding local override
      localsCopy.push(newLocal);
    }

    persistLocal(localsCopy);
    setEditIndex(null);
    setEditEvent({ title: "", date: "", location: "", desc: "" });
  };

  // Cancel edit
  const cancelEdit = () => {
    setEditIndex(null);
    setEditEvent({ title: "", date: "", location: "", desc: "" });
  };

  // Delete — if the event is local => remove from localEvents, if json => add tombstone in localEvents to hide it
  const deleteEvent = (index) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    const ev = events[index];
    const key = eventKey(ev);

    // If event is local, remove it from localEvents
    if (ev.source === "local") {
      const updatedLocal = localEvents.filter((le) => eventKey(le) !== key);
      persistLocal(updatedLocal);
      // adjust editIndex if needed
      if (editIndex === index) cancelEdit();
      return;
    }

    // If event is from JSON, create a 'tombstone' in localEvents to hide it
    // We'll represent tombstone as an object with __deleted: true and the key fields
    const tombstone = { title: ev.title, date: ev.date, __deleted: true, source: "local" };

    // avoid duplicate tombstones:
    const already = localEvents.some((le) => le.__deleted && eventKey(le) === key);
    if (already) return;

    const updatedLocal = [...localEvents, tombstone];
    persistLocal(updatedLocal);

    if (editIndex === index) cancelEdit();
  };

  // Build UI-friendly event list that excludes tombstones and sorts by date asc (optional)
  const uiEvents = events
    .filter((ev) => !(ev.__deleted)) // remove deleted tombstones
    // optional: sort by date (you can remove if you want original order)
    .sort((a, b) => {
      if (!a.date && !b.date) return 0;
      if (!a.date) return 1;
      if (!b.date) return -1;
      return a.date.localeCompare(b.date);
    });

  return (
    <div>
      {/* Hero */}
      <section className="bg-gradient-to-br from-pink-50 to-white border-b">
        <div className="page py-14">
          <div className="grid md:grid-cols-2 items-center gap-10">
            <div>
              <h1 className="text-4xl md:text-5xl font-bold leading-tight">
                Plan, Publish & Manage <span className="text-primary">Events</span> with Ease
              </h1>
              <p className="mt-4 text-gray-600">
                Create events, track registrations, and share updates — all in one simple interface.
              </p>
              <div className="mt-6 flex gap-3">
                <Link to="/register" className="btn btn-primary">Get Started</Link>
                <Link to="/about" className="btn bg-white border">Learn More</Link>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold mb-3">Quick Add Event</h3>
              <form className="grid gap-3" onSubmit={addEvent}>
                <input
                  className="border rounded-xl px-4 py-2.5"
                  placeholder="Event Title"
                  value={newEvent.title}
                  onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                />
                <input
                  type="date"
                  className="border rounded-xl px-4 py-2.5"
                  value={newEvent.date}
                  onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                />
                <input
                  className="border rounded-xl px-4 py-2.5"
                  placeholder="Location"
                  value={newEvent.location}
                  onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
                />
                <textarea
                  className="border rounded-xl px-4 py-2.5"
                  rows="3"
                  placeholder="Description"
                  value={newEvent.desc}
                  onChange={(e) => setNewEvent({ ...newEvent, desc: e.target.value })}
                />
                <button className="btn btn-primary" type="submit">Add Event</button>
              </form>
            </div>
          </div>
        </div>
      </section>

      {/* Events list */}
      <section className="page">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-semibold">Upcoming Events</h2>
          {/* small manual refresh - useful if you edit events.json on disk and want to force re-check */}
          <button
            className="btn bg-white border text-sm"
            onClick={() => {
              // trigger a re-fetch of events.json by clearing cached raw and reloading the page logic:
              localStorage.removeItem(JSON_RAW_KEY);
              // reload component by fetching again:
              window.location.reload();
            }}
          >
            Refresh events.json
          </button>
        </div>

        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {uiEvents.length === 0 && (
            <p className="text-gray-500">No events yet. Add one using the form above.</p>
          )}

          {uiEvents.map((e, i) => (
            <div key={i} className="card">
              {/* If this card is being edited, show the edit form instead */}
              {editIndex === i ? (
                <form className="grid gap-3" onSubmit={saveEdit}>
                  <input
                    className="border rounded-xl px-4 py-2.5"
                    placeholder="Event Title"
                    value={editEvent.title}
                    onChange={(ev) => setEditEvent({ ...editEvent, title: ev.target.value })}
                    required
                  />
                  <input
                    type="date"
                    className="border rounded-xl px-4 py-2.5"
                    value={editEvent.date}
                    onChange={(ev) => setEditEvent({ ...editEvent, date: ev.target.value })}
                    required
                  />
                  <input
                    className="border rounded-xl px-4 py-2.5"
                    placeholder="Location"
                    value={editEvent.location}
                    onChange={(ev) => setEditEvent({ ...editEvent, location: ev.target.value })}
                  />
                  <textarea
                    className="border rounded-xl px-4 py-2.5"
                    rows="3"
                    placeholder="Description"
                    value={editEvent.desc}
                    onChange={(ev) => setEditEvent({ ...editEvent, desc: ev.target.value })}
                  />

                  <div className="flex gap-2">
                    <button className="btn btn-primary" type="submit">Save</button>
                    <button type="button" className="btn bg-white border" onClick={cancelEdit}>Cancel</button>
                  </div>
                </form>
              ) : (
                <>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <img
                        src={uploadedImageUrl}
                        alt="thumb"
                        className="w-12 h-12 rounded-md object-cover"
                        onError={(ev) => { ev.currentTarget.style.display = "none"; }}
                      />
                      <div>
                        <h3 className="text-lg font-semibold">{e.title}</h3>
                        <p className="text-sm text-gray-600">{e.location}</p>
                      </div>
                    </div>

                    <span className="text-xs bg-pink-100 text-pink-600 px-2 py-1 rounded-full">{e.date}</span>
                  </div>

                  <p className="text-sm mt-3">{e.desc}</p>

                  <div className="mt-4 flex gap-2">
                    <button className="btn bg-white border" onClick={() => startEdit(i)}>Edit</button>
                    <button className="btn btn-danger" onClick={() => deleteEvent(i)}>Delete</button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
