import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: "", date: "", location: "", desc: "" });

  // validation state
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  // edit mode
  const [isEditing, setIsEditing] = useState(false);
  const [editingId, setEditingId] = useState(null);

  // Load events
  useEffect(() => {
    fetch("/events.json?time=" + Date.now(), { cache: "no-store" })
      .then((res) => res.json())
      .then((data) => {
        const normalized = (data || []).map((ev) => ({
          id: ev.id || `srv-${(ev.title || "e").replace(/\s+/g, "-")}-${ev.date || Date.now()}`,
          ...ev,
        }));
        setEvents(normalized);
      })
      .catch((err) => console.error("Error loading events:", err));
  }, []);

  // validation helpers
  const isPastDate = (isoDate) => {
    if (!isoDate) return false;
    const input = new Date(isoDate);
    // zero-time compare to local today
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    input.setHours(0, 0, 0, 0);
    return input < today;
  };

  const validateField = (name, value) => {
    switch (name) {
      case "title":
        if (!value || value.trim() === "") return "Title is required.";
        if (value.trim().length < 3) return "Title must be at least 3 characters.";
        return "";
      case "date":
        if (!value) return "Date is required.";
        if (isPastDate(value)) return "Date cannot be in the past.";
        return "";
      case "location":
        if (value && value.trim().length > 0 && value.trim().length < 2) return "Location must be at least 2 characters.";
        return "";
      case "desc":
        if (value && value.length > 400) return "Description must be 400 characters or less.";
        return "";
      default:
        return "";
    }
  };

  const validateAll = (payload = newEvent) => {
    const next = {};
    next.title = validateField("title", payload.title);
    next.date = validateField("date", payload.date);
    next.location = validateField("location", payload.location);
    next.desc = validateField("desc", payload.desc);
    setErrors(next);
    // form valid if no truthy error messages for required fields
    return !next.title && !next.date && !next.location && !next.desc;
  };

  // onChange handler that validates on-the-fly
  const handleChange = (field) => (e) => {
    const value = e.target.value;
    const updated = { ...newEvent, [field]: value };
    setNewEvent(updated);

    // mark touched
    setTouched((t) => ({ ...t, [field]: true }));

    // validate only that field (and we also update overall errors)
    const fieldError = validateField(field, value);
    setErrors((err) => ({ ...err, [field]: fieldError }));
  };

  // Add or Save (edit) event
  const handleSubmit = (e) => {
    e.preventDefault();
    const ok = validateAll();
    if (!ok) {
      // mark all touched to show errors
      setTouched({ title: true, date: true, location: true, desc: true });
      return;
    }

    if (isEditing && editingId) {
      const updated = events.map((ev) => (ev.id === editingId ? { ...ev, ...newEvent, id: editingId } : ev));
      setEvents(updated);
      cancelEdit();
    } else {
      const eventWithId = { ...newEvent, id: `loc-${Date.now()}` };
      const updated = [...events, eventWithId];
      setEvents(updated);
      setNewEvent({ title: "", date: "", location: "", desc: "" });
      setTouched({});
      setErrors({});
    }
  };

  // Start editing
  const startEdit = (id) => {
    const ev = events.find((x) => x.id === id);
    if (!ev) return;
    setNewEvent({ title: ev.title || "", date: ev.date || "", location: ev.location || "", desc: ev.desc || "" });
    setIsEditing(true);
    setEditingId(id);
    setErrors({});
    setTouched({});
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // Cancel editing
  const cancelEdit = () => {
    setIsEditing(false);
    setEditingId(null);
    setNewEvent({ title: "", date: "", location: "", desc: "" });
    setErrors({});
    setTouched({});
  };

  // Delete event
  const handleDelete = (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    const updated = events.filter((ev) => ev.id !== id);
    setEvents(updated);
    if (isEditing && editingId === id) cancelEdit();
  };

  // helper for border class based on validation
  const borderClass = (field) => {
    const base = "rounded-xl px-4 py-2.5 border ";
    if (!touched[field]) return base + "border-gray-200";
    return errors[field] ? base + "border-red-500" : base + "border-green-500";
  };

  const formValid = !validateField("title", newEvent.title) && !validateField("date", newEvent.date);

  return (
    <div>
      {/* Hero */}
      <section className="bg-gradient-to-br from-pink-50 to-white border-b">
        <div className="page py-14">
          <div className="grid md:grid-cols-2 items-center gap-10">
            <div>
              <h1 className="text-4xl md:text-5xl font-bold leading-tight">
                Plan, Publish & Manage <span className="text-primary">Events</span> with Ease
              </h1>
              <p className="mt-4 text-gray-600">
                Create events, track registrations, and share updates â€” all in one simple interface.
              </p>
              <div className="mt-6 flex gap-3">
                <Link to="/register" className="btn btn-primary">Get Started</Link>
                <Link to="/about" className="btn bg-white border">Learn More</Link>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold mb-3">{isEditing ? "Edit Event" : "Quick Add Event"}</h3>
              <form className="grid gap-3" onSubmit={handleSubmit} noValidate>
                <div>
                  <input
                    className={borderClass("title")}
                    placeholder="Event Title"
                    value={newEvent.title}
                    onChange={handleChange("title")}
                    onBlur={() => setTouched((t) => ({ ...t, title: true }))}
                    aria-invalid={!!errors.title}
                    aria-describedby="title-error"
                  />
                  {touched.title && errors.title ? (
                    <div id="title-error" className="text-sm text-red-600 mt-1">{errors.title}</div>
                  ) : touched.title && !errors.title ? (
                    <div className="text-sm text-green-600 mt-1">Looks good.</div>
                  ) : null}
                </div>

                <div>
                  <input
                    type="date"
                    className={borderClass("date")}
                    value={newEvent.date}
                    onChange={handleChange("date")}
                    onBlur={() => setTouched((t) => ({ ...t, date: true }))}
                    aria-invalid={!!errors.date}
                    aria-describedby="date-error"
                  />
                  {touched.date && errors.date ? (
                    <div id="date-error" className="text-sm text-red-600 mt-1">{errors.date}</div>
                  ) : touched.date && !errors.date ? (
                    <div className="text-sm text-green-600 mt-1">Date is valid.</div>
                  ) : null}
                </div>

                <div>
                  <input
                    className={borderClass("location")}
                    placeholder="Location"
                    value={newEvent.location}
                    onChange={handleChange("location")}
                    onBlur={() => setTouched((t) => ({ ...t, location: true }))}
                    aria-invalid={!!errors.location}
                    aria-describedby="location-error"
                  />
                  {touched.location && errors.location ? (
                    <div id="location-error" className="text-sm text-red-600 mt-1">{errors.location}</div>
                  ) : touched.location && !errors.location && newEvent.location ? (
                    <div className="text-sm text-green-600 mt-1">Looks good.</div>
                  ) : null}
                </div>

                <div>
                  <textarea
                    className={borderClass("desc")}
                    rows="3"
                    placeholder="Description"
                    value={newEvent.desc}
                    onChange={handleChange("desc")}
                    onBlur={() => setTouched((t) => ({ ...t, desc: true }))}
                    aria-invalid={!!errors.desc}
                    aria-describedby="desc-error"
                  />
                  {touched.desc && errors.desc ? (
                    <div id="desc-error" className="text-sm text-red-600 mt-1">{errors.desc}</div>
                  ) : touched.desc && !errors.desc && newEvent.desc ? (
                    <div className="text-sm text-green-600 mt-1">Looks good.</div>
                  ) : null}
                </div>

                <div className="flex gap-3">
                  <button
                    className={`btn btn-primary ${!formValid ? "opacity-60 cursor-not-allowed" : ""}`}
                    type="submit"
                    disabled={!formValid}
                    aria-disabled={!formValid}
                  >
                    {isEditing ? "Save Changes" : "Add Event"}
                  </button>

                  {isEditing && (
                    <button type="button" className="btn bg-white border" onClick={cancelEdit}>Cancel</button>
                  )}
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>

      {/* Events List */}
      <section className="page">
        <h2 className="text-2xl font-semibold mb-4">Upcoming Events</h2>
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {events.map((e) => (
            <div key={e.id} className="card">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">{e.title}</h3>
                <span className="text-xs bg-pink-100 text-pink-600 px-2 py-1 rounded-full">{e.date}</span>
              </div>
              <p className="text-sm text-gray-600 mt-1">{e.location}</p>
              <p className="text-sm mt-3">{e.desc}</p>

              <div className="mt-4 flex gap-2">
                <button className="btn bg-white border" onClick={() => startEdit(e.id)}>Edit</button>
                <button className="btn btn-danger bg-white border" onClick={() => handleDelete(e.id)}>Delete</button>
                <button className="ml-auto mt-0 btn bg-white border">View Details</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
