// Home.jsx
import React from "react";

export default function Home({ events }) {
  const handleBook = (event) => {
    // get existing bookings
    const stored = localStorage.getItem("bookings");
    const bookings = stored ? JSON.parse(stored) : [];

    // avoid duplicate booking of same event
    const already = bookings.find((b) => b.id === event.id);
    if (!already) {
      bookings.push({
        id: event.id,
        title: event.title,
        date: event.date,
        location: event.location,
        desc: event.desc,
      });
      localStorage.setItem("bookings", JSON.stringify(bookings));
    }

    alert(`You have booked: ${event.title}`);
  };

  return (
    <div className="min-h-screen bg-pink-50">
      {/* ... your hero / top section ... */}

      <section className="max-w-6xl mx-auto mt-10 px-4">
        <h2 className="text-2xl font-bold mb-4">Upcoming Events</h2>

        <div className="grid md:grid-cols-3 gap-6">
          {events.map((ev) => (
            <div
              key={ev.id}
              className="bg-white rounded-3xl shadow-md p-6 flex flex-col justify-between"
            >
              <div>
                <div className="flex justify-between items-center mb-2">
                  <h3 className="font-semibold text-lg">{ev.title}</h3>
                  <span className="text-xs bg-pink-100 text-pink-700 px-3 py-1 rounded-full">
                    {ev.date}
                  </span>
                </div>
                <p className="text-xs text-gray-500 mb-1">{ev.location}</p>
                <p className="text-sm text-gray-700 mb-4">{ev.desc}</p>
              </div>

              <div className="flex gap-3 mt-2">
                {/* BOOK EVENT BUTTON */}
                <button
                  onClick={() => handleBook(ev)}
                  className="px-4 py-2 rounded-full bg-pink-600 text-white text-sm font-semibold hover:bg-pink-700 transition"
                >
                  Book Event
                </button>

                {/* If you still want Edit / Delete on Home (for admin): */}
                {/* 
                <button className="px-4 py-2 rounded-full border text-sm">
                  Edit
                </button>
                <button className="px-4 py-2 rounded-full border text-sm">
                  Delete
                </button>
                */}
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
