import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: "", date: "", location: "", desc: "" });
  const pollingRef = useRef(null);

  // helper: ensure event has an id
  const ensureId = (ev, prefix = "srv") => {
    if (ev.id) return ev;
    // If server provides no id, create one from title+date (or fallback timestamp)
    const safeTitle = (ev.title || "").trim().replace(/\s+/g, "-").toLowerCase();
    const id = ev.title && ev.date ? `${prefix}-${safeTitle}-${ev.date}` : `${prefix}-${Date.now()}-${Math.random().toString(36).slice(2,6)}`;
    return { ...ev, id };
  };

  // merge server events and local events, prefer local events when ids clash
  const mergeEvents = (serverEvents, localEvents) => {
    // normalize server events to include id
    const srv = serverEvents.map((e) => ensureId(e, "srv"));
    const local = (localEvents || []).map((e) => ensureId(e, "loc"));

    // map local by id for precedence
    const map = new Map();
    local.forEach((e) => map.set(e.id, e));
    // add server events if not present locally
    srv.forEach((e) => {
      if (!map.has(e.id)) map.set(e.id, e);
    });

    // return array sorted by date (optional) newest first
    const merged = Array.from(map.values()).sort((a, b) => {
      // sort by date (ISO) if present, fallback to id
      const da = a.date || "";
      const db = b.date || "";
      if (da && db) return da < db ? -1 : da > db ? 1 : 0;
      return a.id < b.id ? -1 : 1;
    });

    return merged;
  };

  // fetch events (server) and merge with localStorage
  const fetchAndMerge = async () => {
    try {
      const res = await fetch("/events.json", { cache: "no-store" }); // no-store avoids aggressive caching
      if (!res.ok) throw new Error("fetch failed");
      const serverEvents = await res.json();

      const rawLocal = localStorage.getItem("events");
      const localEvents = rawLocal ? JSON.parse(rawLocal) : [];

      const merged = mergeEvents(serverEvents, localEvents);

      // If merged differs from current state, update state and localStorage if needed
      setEvents((prev) => {
        const prevStr = JSON.stringify(prev);
        const mergedStr = JSON.stringify(merged);
        if (prevStr !== mergedStr) {
          // Persist merged list so server-added events also appear persistently
          localStorage.setItem("events", mergedStr);
          return merged;
        }
        return prev;
      });
    } catch (err) {
      console.error("Error fetching events:", err);
      // if fetch fails but local exists, keep using local. If local empty, try fallback file load below (optional).
    }
  };

  // initial load: prefer localStorage if present, otherwise fetch and save
  useEffect(() => {
    const saved = localStorage.getItem("events");
    if (saved) {
      // ensure ids for saved items
      const parsed = JSON.parse(saved).map((e) => ensureId(e, "loc"));
      setEvents(parsed);
      // still trigger a fetch to look for new server events right away
      fetchAndMerge();
    } else {
      // no local copy: fetch server file and store it
      fetchAndMerge();
    }

    // start polling for updates every N ms
    const intervalMs = 7000; // 7 seconds — adjust as needed
    pollingRef.current = setInterval(fetchAndMerge, intervalMs);

    // cleanup
    return () => {
      if (pollingRef.current) clearInterval(pollingRef.current);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once on mount

  // Add new event (user)
  const addEvent = (e) => {
    e.preventDefault();
    if (!newEvent.title || !newEvent.date) return;

    const eventWithId = { ...newEvent, id: `loc-${Date.now()}` };
    const updatedEvents = [...events, eventWithId];
    setEvents(updatedEvents);

    // Save to localStorage
    localStorage.setItem("events", JSON.stringify(updatedEvents));

    setNewEvent({ title: "", date: "", location: "", desc: "" });
  };

  return (
    <div>
      {/* Hero */}
      <section className="bg-gradient-to-br from-pink-50 to-white border-b">
        <div className="page py-14">
          <div className="grid md:grid-cols-2 items-center gap-10">
            <div>
              <h1 className="text-4xl md:text-5xl font-bold leading-tight">
                Plan, Publish & Manage <span className="text-primary">Events</span> with Ease
              </h1>
              <p className="mt-4 text-gray-600">
                Create events, track registrations, and share updates — all in one simple interface.
              </p>
              <div className="mt-6 flex gap-3">
                <Link to="/register" className="btn btn-primary">Get Started</Link>
                <Link to="/about" className="btn bg-white border">Learn More</Link>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold mb-3">Quick Add Event</h3>
              <form className="grid gap-3" onSubmit={addEvent}>
                <input className="border rounded-xl px-4 py-2.5" placeholder="Event Title"
                  value={newEvent.title} onChange={e => setNewEvent({ ...newEvent, title: e.target.value })} />

                <input type="date" className="border rounded-xl px-4 py-2.5"
                  value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} />

                <input className="border rounded-xl px-4 py-2.5" placeholder="Location"
                  value={newEvent.location} onChange={e => setNewEvent({ ...newEvent, location: e.target.value })} />

                <textarea className="border rounded-xl px-4 py-2.5" rows="3" placeholder="Description"
                  value={newEvent.desc} onChange={e => setNewEvent({ ...newEvent, desc: e.target.value })}></textarea>

                <button className="btn btn-primary" type="submit">Add Event</button>
              </form>
            </div>
          </div>
        </div>
      </section>

      {/* Events List */}
      <section className="page">
        <h2 className="text-2xl font-semibold mb-4">Upcoming Events</h2>
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {events.map((e) => (
            <div key={e.id} className="card">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">{e.title}</h3>
                <span className="text-xs bg-pink-100 text-pink-600 px-2 py-1 rounded-full">{e.date}</span>
              </div>
              <p className="text-sm text-gray-600 mt-1">{e.location}</p>
              <p className="text-sm mt-3">{e.desc}</p>
              <button className="mt-4 btn bg-white border">View Details</button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
