import React, { useState } from "react";

export default function Register() {
  const [form, setForm] = useState({ name:"", email:"", password:"", confirm:"" });
  const change = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const submit = (e) => { e.preventDefault(); alert(`Registered ${form.name}`); };

  return (
    <div className="page">
      <div className="max-w-md card mx-auto">
        <h2 className="text-2xl font-semibold mb-4">Create Account</h2>
        <form className="grid gap-3" onSubmit={submit}>
          <input className="border rounded-xl px-4 py-2.5" name="name" placeholder="Full Name" value={form.name} onChange={change} />
          <input className="border rounded-xl px-4 py-2.5" name="email" type="email" placeholder="Email" value={form.email} onChange={change} />
          <input className="border rounded-xl px-4 py-2.5" name="password" type="password" placeholder="Password" value={form.password} onChange={change} />
          <input className="border rounded-xl px-4 py-2.5" name="confirm" type="password" placeholder="Confirm Password" value={form.confirm} onChange={change} />
          <button className="btn btn-primary" type="submit">Register</button>
        </form>
      </div>
    </div>
  );
}
