import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: "", date: "", location: "", desc: "" });

  // Edit state
  const [editIndex, setEditIndex] = useState(null);
  const [editEvent, setEditEvent] = useState({ title: "", date: "", location: "", desc: "" });

  // <-- IMPORTANT: define uploadedImageUrl so JSX doesn't crash
  const uploadedImageUrl = "/mnt/data/83ef8988-4e1a-4cb2-b6c4-7062b81cfdf6.png";

  // Load events: merge JSON + localStorage (defensive)
  useEffect(() => {
    const savedRaw = localStorage.getItem("events");
    let saved = null;

    if (savedRaw) {
      try {
        saved = JSON.parse(savedRaw);
        if (!Array.isArray(saved)) saved = null;
      } catch (err) {
        console.warn("Invalid events in localStorage, ignoring:", err);
        saved = null;
      }
    }

    // Always try to fetch events.json (fallback if fetch fails we still use local)
    fetch("/events.json")
      .then((res) => {
        if (!res.ok) throw new Error("events.json not found");
        return res.json();
      })
      .then((jsonEvents) => {
        // ensure jsonEvents is an array
        const base = Array.isArray(jsonEvents) ? jsonEvents : [];
        // merge: jsonEvents first, then user-saved events (so user additions appear after defaults)
        const merged = saved ? [...base, ...saved] : base;
        setEvents(merged);
      })
      .catch((err) => {
        // if fetch fails but saved exists, use saved
        console.warn("Could not fetch events.json:", err);
        if (saved) setEvents(saved);
        else setEvents([]); // nothing to show
      });
  }, []);

  // persist helper
  const persist = (updated) => {
    setEvents(updated);
    try {
      localStorage.setItem("events", JSON.stringify(updated));
    } catch (err) {
      console.warn("Could not save events to localStorage:", err);
    }
  };

  // Add new event
  const addEvent = (e) => {
    e.preventDefault();
    if (!newEvent.title || !newEvent.date) return;
    const updatedEvents = [...events, newEvent];
    persist(updatedEvents);
    setNewEvent({ title: "", date: "", location: "", desc: "" });
  };

  // Delete event
  const deleteEvent = (index) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    const updated = events.filter((_, i) => i !== index);
    persist(updated);
    // if deleting the currently editing event, cancel edit
    if (editIndex === index) {
      setEditIndex(null);
      setEditEvent({ title: "", date: "", location: "", desc: "" });
    } else if (editIndex !== null && index < editIndex) {
      // shift editIndex when an earlier item is removed
      setEditIndex((prev) => (prev === null ? null : prev - 1));
    }
  };

  // Start editing an event
  const startEdit = (index) => {
    setEditIndex(index);
    setEditEvent({ ...events[index] });
  };

  // Cancel edit
  const cancelEdit = () => {
    setEditIndex(null);
    setEditEvent({ title: "", date: "", location: "", desc: "" });
  };

  // Save edited event
  const saveEdit = (e) => {
    e.preventDefault();
    if (!editEvent.title || !editEvent.date) return;
    const updated = events.map((ev, i) => (i === editIndex ? editEvent : ev));
    persist(updated);
    setEditIndex(null);
    setEditEvent({ title: "", date: "", location: "", desc: "" });
  };

  return (
    <div>
      {/* Hero */}
      <section className="bg-gradient-to-br from-pink-50 to-white border-b">
        <div className="page py-14">
          <div className="grid md:grid-cols-2 items-center gap-10">
            <div>
              <h1 className="text-4xl md:text-5xl font-bold leading-tight">
                Plan, Publish & Manage <span className="text-primary">Events</span> with Ease
              </h1>
              <p className="mt-4 text-gray-600">
                Create events, track registrations, and share updates â€” all in one simple interface.
              </p>
              <div className="mt-6 flex gap-3">
                <Link to="/register" className="btn btn-primary">Get Started</Link>
                <Link to="/about" className="btn bg-white border">Learn More</Link>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold mb-3">Quick Add Event</h3>
              <form className="grid gap-3" onSubmit={addEvent}>
                <input
                  className="border rounded-xl px-4 py-2.5"
                  placeholder="Event Title"
                  value={newEvent.title}
                  onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                />
                <input
                  type="date"
                  className="border rounded-xl px-4 py-2.5"
                  value={newEvent.date}
                  onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                />
                <input
                  className="border rounded-xl px-4 py-2.5"
                  placeholder="Location"
                  value={newEvent.location}
                  onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
                />
                <textarea
                  className="border rounded-xl px-4 py-2.5"
                  rows="3"
                  placeholder="Description"
                  value={newEvent.desc}
                  onChange={(e) => setNewEvent({ ...newEvent, desc: e.target.value })}
                ></textarea>
                <button className="btn btn-primary" type="submit">Add Event</button>
              </form>
            </div>
          </div>
        </div>
      </section>

      {/* Events list */}
      <section className="page">
        <h2 className="text-2xl font-semibold mb-4">Upcoming Events</h2>
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {events.length === 0 && (
            <p className="text-gray-500">No events yet. Add one using the form above.</p>
          )}

          {events.map((e, i) => (
            <div key={i} className="card">
              {/* If this card is being edited, show the edit form instead */}
              {editIndex === i ? (
                <form className="grid gap-3" onSubmit={saveEdit}>
                  <input
                    className="border rounded-xl px-4 py-2.5"
                    placeholder="Event Title"
                    value={editEvent.title}
                    onChange={(ev) => setEditEvent({ ...editEvent, title: ev.target.value })}
                    required
                  />
                  <input
                    type="date"
                    className="border rounded-xl px-4 py-2.5"
                    value={editEvent.date}
                    onChange={(ev) => setEditEvent({ ...editEvent, date: ev.target.value })}
                    required
                  />
                  <input
                    className="border rounded-xl px-4 py-2.5"
                    placeholder="Location"
                    value={editEvent.location}
                    onChange={(ev) => setEditEvent({ ...editEvent, location: ev.target.value })}
                  />
                  <textarea
                    className="border rounded-xl px-4 py-2.5"
                    rows="3"
                    placeholder="Description"
                    value={editEvent.desc}
                    onChange={(ev) => setEditEvent({ ...editEvent, desc: ev.target.value })}
                  ></textarea>

                  <div className="flex gap-2">
                    <button className="btn btn-primary" type="submit">Save</button>
                    <button type="button" className="btn bg-white border" onClick={cancelEdit}>Cancel</button>
                  </div>
                </form>
              ) : (
                <>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      {/* optional thumbnail */}
                      <img
                        src={uploadedImageUrl}
                        alt="thumb"
                        className="w-12 h-12 rounded-md object-cover"
                        onError={(ev) => { ev.currentTarget.style.display = "none"; }}
                      />
                      <div>
                        <h3 className="text-lg font-semibold">{e.title}</h3>
                        <p className="text-sm text-gray-600">{e.location}</p>
                      </div>
                    </div>

                    <span className="text-xs bg-pink-100 text-pink-600 px-2 py-1 rounded-full">{e.date}</span>
                  </div>

                  <p className="text-sm mt-3">{e.desc}</p>

                  <div className="mt-4 flex gap-2">
                    <button className="btn bg-white border" onClick={() => startEdit(i)}>Edit</button>
                    <button className="btn btn-danger" onClick={() => deleteEvent(i)}>Delete</button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
